import React, { useState, useEffect } from "react";
import Header from "../components/Header";
import { Card, CardContent } from "../components/ui/card";
import { Button } from "../components/ui/button";

export default function SavedQuotes({ user }) {
  const [quotes, setQuotes] = useState([]);
  const [filtered, setFiltered] = useState([]);

  useEffect(() => {
    // Replace with real fetch from Supabase
    const sampleQuotes = [
      { id: 1001, customer: "Bill & Ted", date: "2025-04-08", event: "Champs Vegas", rep: "admin@gvws.com" },
      { id: 1002, customer: "Alex", date: "2025-04-07", event: "Champs Chicago", rep: "rep1@gvws.com" },
    ];

    // Only show user's quotes if Sales Rep
    const visibleQuotes =
      user.role === "admin"
        ? sampleQuotes
        : sampleQuotes.filter((q) => q.rep === user.email);

    setQuotes(visibleQuotes);
    setFiltered(visibleQuotes);
  }, [user]);

  return (
    <div className="min-h-screen bg-gray-100">
      <Header title="Saved Quotes" user={user} />

      <div className="max-w-7xl mx-auto p-6 space-y-6">
        {/* Filters placeholder */}
        <Card>
          <CardContent className="p-4 space-y-4">
            <div className="grid grid-cols-3 gap-4">
              <input type="date" className="border p-2 rounded" />
              <select className="border p-2 rounded">
                <option>All Sales Reps</option>
                <option>admin@gvws.com</option>
                <option>rep1@gvws.com</option>
              </select>
              <select className="border p-2 rounded">
                <option>All Events</option>
              </select>
            </div>
            <Button className="bg-blue-500 text-white">Apply Filters</Button>
          </CardContent>
        </Card>

        {/* Quotes list */}
        {filtered.map((quote) => (
          <Card key={quote.id}>
            <CardContent className="p-4 flex justify-between items-center">
              <div>
                <div className="font-semibold">Quote #{quote.id}</div>
                <div className="text-sm text-gray-500">
                  {quote.customer} – {quote.date} – {quote.event}
                </div>
              </div>
              <Button>Edit</Button>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}
