import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import NewQuote from './screens/NewQuote'; // Correct import path
import SavedQuotes from './screens/SavedQuotes'; // Correct import path
import Landing from './screens/Landing'; // Correct import path
import Login from './screens/Login'; // Correct import path
import Reports from "./screens/Reports";
import Settings from "./screens/Settings";


const App = () => {
  const [user, setUser] = useState({ username: "admin1", role: "admin" });

  useEffect(() => {
    const loggedUser = localStorage.getItem('user');
    if (loggedUser) {
      setUser(JSON.parse(loggedUser));
    }
  }, []); // Set user from localStorage or session

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={user ? <Landing /> : <Login onLogin={setUser} />}
        />
        <Route
          path="/new-quote"
          element={user ? <NewQuote user={user} /> : <Navigate to="/" />}
        />
        <Route
          path="/saved-quotes"
          element={user ? <SavedQuotes user={user} /> : <Navigate to="/" />}
        />
        <Route path="/reports" element={user ? <Reports /> : <Navigate to="/" />} />
<Route path="/settings" element={user && user.role === 'admin' ? <Settings /> : <Navigate to="/" />} />

      </Routes>
    </Router>
  );
};

export default App;
