import React, { useEffect, useState } from "react";
import { Button } from "../components/ui/button";
import { Card, CardContent } from "../components/ui/card";

const SavedQuotes = () => {
  const [quotes, setQuotes] = useState([]);
  const [filteredQuotes, setFilteredQuotes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const stored = localStorage.getItem("quotes");
    if (stored) {
      const parsed = JSON.parse(stored);
      setQuotes(parsed);
      setFilteredQuotes(parsed);
    }
  }, []);

  useEffect(() => {
    const filtered = quotes.filter((quote) =>
      quote.customer?.name?.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredQuotes(filtered);
  }, [searchTerm, quotes]);

  const handleEdit = (quote) => {
    localStorage.setItem("activeQuote", JSON.stringify(quote));
    window.location.href = "/new-quote";
  };

  return (
    <div className="p-6 space-y-4">
      <h1 className="text-2xl font-bold mb-4">Saved Quotes</h1>

      <input
        type="text"
        className="p-2 border rounded w-full max-w-md mb-4"
        placeholder="Search by customer name..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {filteredQuotes.length === 0 ? (
        <p className="text-gray-600">No saved quotes found.</p>
      ) : (
        <div className="space-y-4">
          {filteredQuotes.map((quote) => (
            <Card key={quote.id} className="p-4">
              <CardContent className="space-y-2">
                <div className="flex justify-between">
                  <div>
                    <p className="font-semibold">{quote.customer.name}</p>
                    <p className="text-sm text-gray-500">{quote.customer.phone}</p>
                  </div>
                  <div className="text-right">
                    <p className="text-sm text-gray-500">
                      {new Date(quote.date).toLocaleDateString()}
                    </p>
                    <p className="font-semibold">${quote.total.toFixed(2)}</p>
                  </div>
                </div>
                <div className="text-sm text-gray-600">
                  Items: {quote.items.length} | Sales Rep: {quote.salesRep}
                </div>
                <div className="pt-2">
                  <Button onClick={() => handleEdit(quote)}>Edit</Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};

export default SavedQuotes;
