import React, { useEffect, useState } from "react";
import Papa from "papaparse";
import SearchBar from "../components/SearchBar";
import TrashIcon from "../components/TrashIcon";
import Modal from "../components/Modal";
import Header from "../components/Header";
import { Button } from "../components/ui/button";
import { Card, CardContent } from "../components/ui/card";

const NewQuote = ({ user }) => {
  const [items, setItems] = useState([]);
  const [quoteItems, setQuoteItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [customer, setCustomer] = useState({
    name: "",
    phone: "",
    address1: "",
    address2: "",
    city: "",
    state: "",
    zip: "",
    email: "",
    contactFirstName: "",
    contactLastName: "",
  });
  const [eventList, setEventList] = useState([]);
  const [event, setEvent] = useState("");
  const [date, setDate] = useState(new Date().toISOString().split("T")[0]);
  const [showModal, setShowModal] = useState(false);

  const isAdmin = user.role === "admin";

  const handleQuantityChange = (index, value) => {
    const updated = [...quoteItems];
    updated[index].quantity = parseInt(value) || 0;
    setQuoteItems(updated);
  };

  const handlePriceChange = (index, value) => {
    const updated = [...quoteItems];
    updated[index].price = parseFloat(value) || 0;
    setQuoteItems(updated);
  };

  const removeItem = (index) => {
    const updated = [...quoteItems];
    updated.splice(index, 1);
    setQuoteItems(updated);
  };

  const clearQuote = () => {
    setShowModal(true);
  };

  const confirmClear = () => {
    setQuoteItems([]);
    setCustomer({
      name: "",
      phone: "",
      address1: "",
      address2: "",
      city: "",
      state: "",
      zip: "",
      email: "",
      contactFirstName: "",
      contactLastName: "",
    });
    setEvent("");
    setShowModal(false);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Header title="New Quote" user={user} />

      <div className="max-w-7xl mx-auto p-6 space-y-6">
        {/* Customer Info Box */}
        <Card>
          <CardContent className="space-y-4 p-4">
            <div className="font-semibold text-lg">Customer Info</div>
            <div className="grid grid-cols-2 gap-4">
              {/* Left */}
              <div className="space-y-2">
                <input
                  type="text"
                  placeholder="Name"
                  value={customer.name}
                  onChange={(e) => setCustomer({ ...customer, name: e.target.value })}
                  disabled={!isAdmin}
                  className="border px-3 py-2 rounded w-full"
                />
                <input
                  type="text"
                  placeholder="Address 1"
                  value={customer.address1}
                  onChange={(e) => setCustomer({ ...customer, address1: e.target.value })}
                  disabled={!isAdmin}
                  className="border px-3 py-2 rounded w-full"
                />
                <input
                  type="text"
                  placeholder="Address 2"
                  value={customer.address2}
                  onChange={(e) => setCustomer({ ...customer, address2: e.target.value })}
                  disabled={!isAdmin}
                  className="border px-3 py-2 rounded w-full"
                />
                <input
                  type="text"
                  placeholder="City, State, Zip"
                  value={`${customer.city}, ${customer.state} ${customer.zip}`}
                  onChange={() => {}}
                  disabled
                  className="border px-3 py-2 rounded w-full bg-gray-100"
                />
              </div>

              {/* Right */}
              <div className="space-y-2">
                <input
                  type="text"
                  placeholder="Phone"
                  value={customer.phone}
                  onChange={(e) => setCustomer({ ...customer, phone: e.target.value })}
                  disabled={!isAdmin}
                  className="border px-3 py-2 rounded w-full"
                />
                <input
                  type="text"
                  placeholder="Email"
                  value={customer.email}
                  onChange={(e) => setCustomer({ ...customer, email: e.target.value })}
                  disabled={!isAdmin}
                  className="border px-3 py-2 rounded w-full"
                />
                <input
                  type="text"
                  placeholder="Contact First Name"
                  value={customer.contactFirstName}
                  onChange={(e) => setCustomer({ ...customer, contactFirstName: e.target.value })}
                  disabled={!isAdmin}
                  className="border px-3 py-2 rounded w-full"
                />
                <input
                  type="text"
                  placeholder="Contact Last Name"
                  value={customer.contactLastName}
                  onChange={(e) => setCustomer({ ...customer, contactLastName: e.target.value })}
                  disabled={!isAdmin}
                  className="border px-3 py-2 rounded w-full"
                />
              </div>
            </div>

            {/* Date + Event */}
            <div className="grid grid-cols-2 gap-4 pt-4">
              <input
                type="date"
                value={date}
                onChange={(e) => setDate(e.target.value)}
                disabled={!isAdmin}
                className="border px-3 py-2 rounded w-full"
              />
              <select
                value={event}
                onChange={(e) => setEvent(e.target.value)}
                disabled={!isAdmin}
                className="border px-3 py-2 rounded w-full"
              >
                <option value="">Select Event</option>
                {eventList.map((e) => (
                  <option key={e} value={e}>
                    {e}
                  </option>
                ))}
              </select>
            </div>
          </CardContent>
        </Card>

        <div className="grid grid-cols-2 gap-6">
          {/* Item Search */}
          <Card className="h-[500px] overflow-y-auto">
            <CardContent className="p-4">
              <div className="font-semibold mb-2">Item Search</div>
              <SearchBar
                items={items}
                setFiltered={setItems}
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
              />
            </CardContent>
          </Card>

          {/* Quote Items */}
          <Card className="h-[500px] flex flex-col">
            <CardContent className="p-4 flex-grow flex flex-col">
              <div className="font-semibold mb-2">Items in Quote</div>

              <div className="grid grid-cols-5 gap-2 font-semibold text-sm text-gray-600 border-b pb-2 mb-2">
                <div>Item</div>
                <div>Qty</div>
                <div>Price</div>
                <div>Total</div>
                <div>Remove</div>
              </div>

              <div className="flex-grow overflow-y-auto space-y-2">
                {quoteItems.map((item, index) => (
                  <div key={index} className="grid grid-cols-5 gap-2 items-center">
                    <div className="font-medium">{item.name}</div>

                    <input
                      type="text"
                      inputMode="numeric"
                      pattern="[0-9]*"
                      value={item.quantity}
                      onChange={(e) => handleQuantityChange(index, e.target.value)}
                      className="border rounded-md px-2 py-1 text-center"
                    />

                    <input
                      type="text"
                      inputMode="decimal"
                      pattern="^\d+(\.\d{0,2})?$"
                      value={item.price}
                      onChange={(e) => handlePriceChange(index, e.target.value)}
                      className="border rounded-md px-2 py-1 text-center"
                    />

                    <div className="text-right pr-2">
                      ${(item.quantity * item.price).toFixed(2)}
                    </div>

                    <button onClick={() => removeItem(index)}>
                      <TrashIcon />
                    </button>
                  </div>
                ))}
              </div>

              <div className="pt-4 mt-4 border-t flex justify-end space-x-4">
                <Button variant="outline" onClick={clearQuote}>
                  Clear Quote
                </Button>
                <Button onClick={() => alert("Save logic here")}>Save Quote</Button>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Confirm Clear Modal */}
      <Modal isOpen={showModal} onClose={() => setShowModal(false)} onConfirm={confirmClear}>
        <p>Are you sure you want to clear the quote? All unsaved data will be lost.</p>
      </Modal>
    </div>
  );
};

export default NewQuote;
